const COLORS = {
  orange10: "#FEEAD3",
  orange20: "#FED0A8",
  orange30: "#FEB07C",
  orange40: "#FE905C",
  orange50: "#FE5D26",
  orange60: "#DA3E1B",
  orange70: "#B62513",
  orange80: "#93120C",
  orange90: "#79070A",
  rgba: "rgba(254, 93, 38, 0.5)",
  backgound10: "#F6F8FC",
  backgound20: "#F1F4F9",
  backgound30: "#E2E8F0",
  boder40: "#CBD4E1",
  boder50: "#94A3B8",
  lightfonts60: "##64748B",
  lightfonts70: "#475569",
  bodyfonts80: "#27364B",
  bodyfonts90: "#1E2A3B",
  darkfonts100: "#0F1A2A",
};

const FONT = {
  regular: "BeVietnamPro-Regular",
  medium: "BeVietnamPro-Medium",
  bold: "BeVietnamPro-Bold",
};

const SIZES = {
  xSmall: 10,
  small: 12,
  medium: 16,
  large: 20,
  xLarge: 24,
  xxLarge: 32,
};

const SHADOWS = {
  small: {
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 2,
  },
  medium: {
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 5.84,
    elevation: 5,
  },
};

export { COLORS,SHADOWS,SIZES,FONT };
