import React, { useContext, useState } from "react";
import { Image } from "expo-image";
import "react-native-gesture-handler";
import {
  StyleSheet,
  View,
  Text,
  Pressable,
  Button,
  TouchableOpacity,
  TextInput,
} from "react-native";
import Modal from "react-native-modal";
import axios from "axios";
import { useNavigation } from "@react-navigation/native";
import { AuthContext } from "../../context/AuthContext";
import Spinner from "react-native-loading-spinner-overlay/lib";
const GoogleLogo = require("../../assets/google.png");

const SignupModal = ({ navigation }) => {
  
  // const navigation = useNavigation();

  // const handleRegistration = () => {
  //   try {
  //     const userData = {
  //       name: name,
  //       username: username,
  //       email: email,
  //       password: password,
  //     };
  //     console.log(userData);
  //     if(userData.name != "" && userData.username != "" && userData.email != "" && userData.password != ""){
  //       setErrorMessage("")
  //       axios
  //       .post(
  //         "http://192.168.2.2:8448/api/v1/auth/register",
  //         userData
  //       )
  //       .then((response) => {
  //         if(response.data){
  //           console.log("Đăng ký thành công! " + response.data.accessToken);
  //           handleNavigation()
  //         } else {
  //           console.log("Đăng ký thất bại");
  //           setErrorMessage("Tài khoản đã tồn tại!")
  //         }

  //       }).catch(
  //         setErrorMessage("Tài khoản đã tồn tại!")
  //       ); // Thay thế 'URL_API' bằng URL API Swagger hoặc API khác
  //     } else {
  //       setErrorMessage("Vui lòng nhập đầy đủ thông tin!")
  //     }

  //     // Chuyển hướng đến trang thành công hoặc thực hiện các tác vụ khác
  //   } catch (error) {
  //     console.error(error); // Xử lý lỗi
  //   }
  // };
  const [name, setName] = useState("");
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [isModalVisible, setIsModalVisible] = useState(false);
  
  const toggleModal = () => {
    setIsModalVisible(!isModalVisible);
  };

  // -----------------------------------------
  const { register, isLoading, userTokenRegister } = useContext(AuthContext);
  const handleRegister = () => {
    register(name, username, email, password);
    if (userTokenRegister.accessToken) {
      navigation.push("SuccessSignupScreen");
    }
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity style={styles.buttonSignup} onPress={toggleModal}>
        <Text style={styles.buttonSignupText}>Đăng ký tài khoản</Text>
      </TouchableOpacity>
      <Modal
        isVisible={isModalVisible}
        onSwipeComplete={toggleModal}
        swipeDirection={["down"]}
        style={styles.modal}
      >
        <View style={styles.modalContent}>
          <Spinner visible={isLoading} />
          <View>
            <View style={styles.underlineTop}></View>

            <View style={styles.signContainer}>
              <View style={styles.modalTextContainer}>
                <Text style={styles.modalText1}>Đăng ký</Text>
                {/* <Text style={styles.modalText2}>Đăng nhập</Text> */}
              </View>
              <View style={styles.underlineContainer}>
                <Text style={styles.underline1}></Text>
                {/* <Text style={styles.underline2}></Text> */}
              </View>
            </View>

            <View>
              <Text style={styles.titleText}>Tên đầy đủ</Text>
              <TextInput
                style={styles.containerInputText}
                placeholder="Nhập tên đầy đủ"
                value={name}
                onChangeText={(text) => setName(text)}
              />
              <Text style={styles.titleText}>Tên đăng nhập</Text>
              <TextInput
                style={styles.containerInputText}
                placeholder="Nhập tên đăng nhập"
                value={username}
                onChangeText={(text) => setUsername(text)}
              />
              <Text style={styles.titleText}>Email</Text>
              <TextInput
                style={styles.containerInputText}
                placeholder="Nhập email"
                value={email}
                onChangeText={(text) => setEmail(text)}
              />
              <Text style={styles.titleText}>Mật khẩu</Text>
              <TextInput
                style={styles.containerInputText}
                placeholder="Nhập mật khẩu"
                value={password}
                secureTextEntry
                onChangeText={(text) => setPassword(text)}
              />
            </View>

            <Text style={styles.errorMessageText}>{errorMessage}</Text>

            <View style={styles.listButtonContainer}>
              <View>
                <TouchableOpacity
                  style={styles.buttonSignup}
                  onPress={handleRegister}
                >
                  <Text style={styles.buttonSignupText}>Đăng ký</Text>
                </TouchableOpacity>
              </View>

              <Text style={styles.underline}></Text>

              <View>
                <TouchableOpacity
                  style={styles.buttonSignupGoogle}
                  onPress={""}
                >
                  <Image
                    style={{ width: 32, height: 32 }}
                    source={GoogleLogo}
                  />
                  <Text style={styles.buttonSignupGoogleText}>
                    Đăng nhập với Google
                  </Text>
                </TouchableOpacity>
              </View>
            </View>
          </View>
        </View>
      </Modal>
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
  buttonContainer: {
    marginTop: 30,
  },
  modal: {
    flex: 1,
    justifyContent: "flex-end",
    margin: 0,
  },
  modalContent: {
    backgroundColor: "#FFFFFF",
    padding: 20,
    height: 750,
    borderTopLeftRadius: 20,
    borderTopRightRadius: 20,
  },
  signContainer: {
    marginBottom: 20,
  },
  modalTextContainer: {
    flexDirection: "row",
  },
  modalText1: {
    fontSize: 18,
    marginBottom: 10,
    fontWeight: "bold",
  },
  modalText2: {
    fontSize: 18,
    marginBottom: 10,
    fontWeight: "bold",
    color: "#CBD4E1",
    marginLeft: 11,
  },
  closeButton: {
    backgroundColor: "#FE5D26",
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 10,
    alignSelf: "flex-end",
  },
  closeButtonText: {
    fontSize: 16,
    color: "#FFFFFF",
    fontWeight: "bold",
  },
  underlineTop: {
    backgroundColor: "#CBD4E1",
    height: 7,
    width: "20%",
    alignSelf: "center",
    marginBottom: 30,
    borderRadius: 30,
  },
  underlineContainer: {
    flexDirection: "row",
  },
  underline1: {
    height: 4,
    backgroundColor: "orange",
    width: "22%",
    borderRadius: 30,
    zIndex: 3,
  },
  underline2: {
    height: 4,
    backgroundColor: "#CBD4E1",
    width: "22%",
    zIndex: 2,
  },
  containerInputText: {
    borderWidth: 1,
    borderColor: "#ccc",
    borderRadius: 10,
    paddingHorizontal: 20,
    paddingVertical: 17,
    marginBottom: 10,
    borderColor: "#CBD4E1",
    fontSize: 16,
  },
  input: {},
  titleText: {
    fontSize: 15,
    marginBottom: 4,
  },
  listButtonContainer: {
    marginTop: 20,
  },
  buttonSignup: {
    paddingVertical: 21,
    paddingHorizontal: 20,
    width: 300,
    alignSelf: "center",
    backgroundColor: "#FE5D26",
    textColor: "#0D0D12",
    borderRadius: 10,
    marginTop: 10,
  },
  buttonSignupText: {
    fontSize: 16,
    fontWeight: "bold",
    textAlign: "center",
  },
  buttonSignupGoogle: {
    flexDirection: "row",
    alignItems: "center",
    paddingVertical: 15,
    paddingHorizontal: 20,
    width: 300,
    alignSelf: "center",
    backgroundColor: "#E2E8F0",
    textColor: "#0D0D12",
    borderRadius: 10,
    marginTop: 10,
  },
  buttonSignupGoogleText: {
    fontSize: 16,
    fontWeight: "bold",
    textAlign: "center",
    marginLeft: 18,
  },
  underline: {
    backgroundColor: "#CBD4E1",
    height: 1,
    width: "50%",
    alignSelf: "center",
    marginTop: 10,
  },
  errorMessageText: {
    color: "red",
    marginTop: 10,
    textAlign: "center",
  },
});
export default SignupModal;
